def edit_medicine(request, id):
    # Get the specific medicine to be edited
    medicine = Medicine.objects.get(id=id)
    if request.method == "POST":
         medicine.name = request.POST.get('name')
         medicine.quantity = request.POST.get('quantity')
         medicine.price = request.POST.get('price')
         medicine.save()
         return redirect('medicine')  # Redirect to the medicine list page
    return render(request, 'edit_medicine.html', {'medicine': medicine})
def add_medicine(request):
    if request.method == "POST":
        name = request.POST.get('name')
        quantity = request.POST.get('quantity')
        price = request.POST.get('price')
        discount = request.POST.get('discount')
        new_medicine = Medicine(name=name, quantity=quantity, price=price, discount=discount)
        new_medicine.save()
        return redirect('medicine')  # Redirect to the medicines list after adding
    return render(request, 'add_medicine.html')


