from django.shortcuts import render
from .models import Medicine, Patient, Doctor

def prescription_and_bill(request):
    # Get the patient and doctor details
    patient_name = request.POST.get('patient_name')
    patient_id = request.POST.get('patient_id')
    patient_contact = request.POST.get('contact_number')
    doctor_name = request.POST.get('doctor_name')
    doctor_specialization = request.POST.get('doctor_specialization')

    # Get the selected medicines
    medicine_ids = request.POST.getlist('medicine_ids')
    selected_medicines = Medicine.objects.filter(id__in=medicine_ids)
    
    # Calculate the total bill
    total_bill = sum([medicine.price for medicine in selected_medicines])

    context = {
        'patient_name': patient_name,
        'patient_id': patient_id,
        'patient_contact': patient_contact,
        'doctor_name': doctor_name,
        'doctor_specialization': doctor_specialization,
        'selected_medicines': selected_medicines,
        'total_bill': total_bill
    }

    return render(request, 'prescription.html', context)
