from django.shortcuts import render, get_object_or_404, redirect
from .models import Patient, Appointment, Invoice
from .forms import InvoiceForm

def generate_invoice(request):
    patient_id = request.GET.get('patient_id')
    patient = appointment = None
    error = None

    if patient_id:
        try:
            patient = get_object_or_404(Patient, patient_id=patient_id)
            appointment = Appointment.objects.filter(patient=patient).latest('appointment_date')
        except Appointment.DoesNotExist:
            error = "No appointment found for the given patient."

    if request.method == 'POST':
        form = InvoiceForm(request.POST)
        if form.is_valid():
            invoice = form.save(commit=False)
            invoice.patient = patient
            invoice.save()
            return redirect('invoice_detail', invoice_id=invoice.id)
    else:
        form = InvoiceForm()

    context = {
        'form': form,
        'patient': patient,
        'appointment': appointment,
        'error': error
    }
    return render(request, 'generate_invoice.html', context)




