def invoice_by_patient(request):
    patient_id = request.GET.get('patient_id')
    invoice = None

    if patient_id:
        try:
            patient = get_object_or_404(Patient, patient_id=patient_id)
            invoice = Invoice.objects.filter(patient=patient).latest('created_at')  # Needs created_at field
        except:
            invoice = None  # optionally pass error message

    return render(request, "invoice_details.html", {"invoice": invoice})

